echo -e "sample_ID\tTotalReads\tMappedReads\tFluA_type\tReads_A_HA\tReads_A_MP\tReads_A_NA\tReads_A_NP\tReads_A_NS\tReads_A_PA\tReads_A_PB1\tReads_A_PB2" > irma_stats.txt; cat ../samples_id.txt | while read in; do paste <(echo ${in}) <(grep '1-initial' ${in}/tables/READ_COUNTS.txt | cut -f2) <(grep '3-match' ${in}/tables/READ_COUNTS.txt | cut -f2) <(paste <(grep '4-A_HA' ${in}/tables/READ_COUNTS.txt | cut -f1 | cut -d '_' -f3) <(grep '4-A_NA' ${in}/tables/READ_COUNTS.txt | cut -f1 | cut -d '_' -f3) | tr '\t' '_') <(grep '4-A_HA' ${in}/tables/READ_COUNTS.txt | cut -f2) <(grep '4-A_MP' ${in}/tables/READ_COUNTS.txt | cut -f2) <(grep '4-A_NA' ${in}/tables/READ_COUNTS.txt | cut -f2) <(grep '4-A_NP' ${in}/tables/READ_COUNTS.txt | cut -f2) <(grep '4-A_NS' ${in}/tables/READ_COUNTS.txt | cut -f2) <(grep '4-A_PA' ${in}/tables/READ_COUNTS.txt | cut -f2) <(grep '4-A_PB1' ${in}/tables/READ_COUNTS.txt | cut -f2) <(grep '4-A_PB2' ${in}/tables/READ_COUNTS.txt | cut -f2); done >> irma_stats.txt
